  <script>
    // Keberangkatan
    $(document).ready(function () {
      $('#keberangkatan').select2({
        placeholder: "Pilih Tahun",
        allowClear: true,
        width: '50%'
      });

      $('#keberangkatan').on('change', function () {
        var tahunId = $(this).val();
        var url = $(this).data('url');

        if (tahunId) {
          $.ajax({
            url: url,
            type: 'GET',
            data: { tahun_id: tahunId },
            success: function (response) {
              $('#jumlahJamaah').text(response.jumlah);
            }
          });
        } else {
          $('#jumlahJamaah').text(0);
        }
      });
    });

    // Keberangkatan Laki-Laki
    $(document).ready(function () {
      $('#keberangkatan_laki').select2({
