        if (tahunId) {
          $.ajax({
            url: url,
            type: 'GET',
            data: { tahun_id: tahunId },
            success: function (response) {
              $('#jamaahPerempuan').text(response.jumlah);
            }
          });
        } else {
          $('#jamaahPerempuan').text(0);
        }
      });
    });

  </script>
